settings.cellStyleFunction:
var type = value;
if(type === 'ESP') return {color: '#4CAF50'};
if(type === 'SRP') return {color: '#2196F3'};
if(type === 'PCP') return {color: '#FF9800'};
if(type === 'gas_lift' || type === 'Gas Lift') return {color: '#9C27B0'};
return {};

if(value === 'producing') return {color: '#4CAF50', fontWeight: '500'};
if(value === 'shut_in') return {color: '#F44336', fontWeight: '500'};
return {};

rowClick.customFunction:
