var status = data['status'] || 'N/A';
var flow = data['flow_rate_bpd'] ? Number(data['flow_rate_bpd']).toFixed(0) : '--';
var field = data['field_name'] || 'N/A';
var liftColors = {'ESP':'#4CAF50','SRP':'#2196F3','PCP':'#FF9800','gas_lift':'#9C27B0'};
var liftColor = liftColors[lift] || '#607D8B';
var statusColor = status === 'producing' ? '#4CAF50' : '#F44336';
var statusLabel = status === 'producing' ? 'Produciendo' : 'Detenido';
return '<div style="font-family:Roboto,sans-serif;padding:12px;min-width:200px;">' +
  '<div style="font-weight:700;font-size:15px;color:#1a2332;margin-bottom:8px;border-bottom:1px solid #eee;padding-bottom:6px;">${entityName}</div>' +
  '<div style="display:grid;grid-template-columns:auto 1fr;gap:4px 10px;font-size:12px;color:#555;">' +
    '<span style="color:#999;">Tipo</span><span style="display:inline-block;padding:2px 8px;border-radius:10px;background:'+liftColor+';color:#fff;font-size:10px;font-weight:600;">' + lift + '</span>' +
    '<span style="color:#999;">Campo</span><span>' + field + '</span>' +
    '<span style="color:#999;">Produccion</span><span style="font-weight:600;">' + flow + ' BPD</span>' +
    '<span style="color:#999;">Estado</span><span style="color:'+statusColor+';font-weight:600;">' + statusLabel + '</span>' +
  '</div>' +
  '<div style="margin-top:8px;text-align:center;"><link-act name="ver-detalle">Ver Detalle &rarr;</link-act></div></div>';

